import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState({ firstName: '', lastName: '', balance: 0 });

    const fetchUsers = async () => {
        try {
            const response = await fetch('https://localhost:7023/api/UserList/all');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setUsers(data.sort((a, b) => a.id - b.id)); // Id'ye göre sıralama
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    const handleAddUser = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('https://localhost:7023/api/Users', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(newUser)
            });
            if (!response.ok) {
                throw new Error('Failed to add user');
            }
            const result = await response.json();
            console.log(result.Message);

            // Yeni kullanıcı eklendiğinde listeyi güncelle
            fetchUsers();
            setNewUser({ firstName: '', lastName: '', balance: 0 });
        } catch (error) {
            console.error('There was a problem with the add user operation:', error);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    return (
        <div className="container">
            <div className="user-list">
                <h2>User List</h2>
                <ul>
                    {users.map(user => (
                        <li key={user.id}>
                            {user.id}: {user.firstName} {user.lastName} - Balance: {user.balance}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="money-transfer">
                <h2>Money Transfer</h2>
                <form id="transferForm">
                    <div className="form-group">
                        <label htmlFor="sender">Sender:</label>
                        <input type="number" id="sender" placeholder="Enter sender's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="receiver">Receiver:</label>
                        <input type="number" id="receiver" placeholder="Enter receiver's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Amount:</label>
                        <input type="number" id="amount" placeholder="Enter amount" required />
                    </div>
                    <button type="submit">Transfer Money</button>
                </form>
            </div>
            <div className="add-user">
                <h2>Add User</h2>
                <form onSubmit={handleAddUser}>
                    <div className="form-group">
                        <label htmlFor="firstName">First Name:</label>
                        <input
                            type="text"
                            id="firstName"
                            value={newUser.firstName}
                            onChange={(e) => setNewUser({ ...newUser, firstName: e.target.value })}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="lastName">Last Name:</label>
                        <input
                            type="text"
                            id="lastName"
                            value={newUser.lastName}
                            onChange={(e) => setNewUser({ ...newUser, lastName: e.target.value })}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="balance">Balance:</label>
                        <input
                            type="number"
                            id="balance"
                            value={newUser.balance}
                            onChange={(e) => setNewUser({ ...newUser, balance: parseFloat(e.target.value) })}
                            required
                        />
                    </div>
                    <button type="submit">Add User</button>
                </form>
            </div>
        </div>
    );
}

export default App;
