import React, { useEffect, useState } from 'react';
import './App.css';
import './TransferPage.css';

function App() {
    const [users, setUsers] = useState([]);
    const [showTransfer, setShowTransfer] = useState(false);
    const [popup, setPopup] = useState(null);

    const fetchUsers = async () => {
        try {
            const response = await fetch('https://localhost:7023/api/UserList/all');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setUsers(data);
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    const handleTransfer = async (e) => {
        e.preventDefault();
        const senderId = parseInt(e.target.sender.value);
        const receiverId = parseInt(e.target.receiver.value);
        const amount = parseFloat(e.target.amount.value);

        try {
            const response = await fetch('https://localhost:7023/api/Transfer', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ senderId, receiverId, amount }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const result = await response.json();
            setPopup({ type: 'success', message: result.message });
            fetchUsers(); // Refresh user list after successful transfer
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
            setPopup({ type: 'error', message: 'Transfer failed. Please check the details and try again.' });
        }
    };

    const handleToggleTransfer = () => {
        setShowTransfer(!showTransfer);
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    return (
        <div className="container">
            <div className="user-list fadeIn">
                <h2>User List</h2>
                <ul>
                    {users.map(user => (
                        <li key={user.id}>
                            ID: {user.id} - {user.firstName} {user.lastName} - Balance: {user.balance}
                        </li>
                    ))}
                </ul>
            </div>
            <div className={`money-transfer ${showTransfer ? 'open' : ''}`}>
                <h2>Money Transfer</h2>
                <form onSubmit={handleTransfer}>
                    <div className="form-group">
                        <label htmlFor="sender">Sender:</label>
                        <input type="number" id="sender" placeholder="Enter sender's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="receiver">Receiver:</label>
                        <input type="number" id="receiver" placeholder="Enter receiver's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Amount:</label>
                        <input type="number" id="amount" placeholder="Enter amount" required />
                    </div>
                    <button type="submit">Transfer Money</button>
                </form>
            </div>
            <button onClick={handleToggleTransfer}>
                {showTransfer ? 'Hide Transfer Form' : 'Show Transfer Form'}
            </button>

            {popup && (
                <div className={`popup ${popup.type}`}>
                    {popup.message}
                </div>
            )}
        </div>
    );
}

export default App;
