import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
    const [users, setUsers] = useState([]);

    const fetchUsers = async () => {
        try {
            const response = await fetch('https://localhost:7023/api/UserList/all');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setUsers(data);
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    const handleTransfer = async (event) => {
        event.preventDefault();

        const senderId = document.getElementById('sender').value;
        const receiverId = document.getElementById('receiver').value;
        const amount = document.getElementById('amount').value;

        const requestBody = {
            senderId: parseInt(senderId, 10),
            receiverId: parseInt(receiverId, 10),
            amount: parseFloat(amount),
        };

        try {
            const response = await fetch('https://localhost:7023/api/Transfer/transfer', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestBody),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            // Başarı durumunda kullanıcı listesini güncelle
            fetchUsers();

        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    return (
        <div className="container">
            <div className="user-list">
                <h2>User List</h2>
                <ul>
                    {users.map(user => (
                        <li key={user.id}>
                            ID: {user.id} - {user.firstName} {user.lastName} - Balance: {user.balance}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="money-transfer">
                <h2>Money Transfer</h2>
                <form id="transferForm" onSubmit={handleTransfer}>
                    <div className="form-group">
                        <label htmlFor="sender">Sender:</label>
                        <input type="number" id="sender" placeholder="Enter sender's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="receiver">Receiver:</label>
                        <input type="number" id="receiver" placeholder="Enter receiver's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Amount:</label>
                        <input type="number" id="amount" placeholder="Enter amount" required />
                    </div>
                    <button type="submit">Transfer Money</button>
                </form>
            </div>
        </div>
    );
}

export default App;
