import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
    const [users, setUsers] = useState([]);
    const [newUser, setNewUser] = useState({
        firstName: '',
        lastName: '',
        balance: 0,
    });

    // Kullanıcıları çek
    const fetchUsers = async () => {
        try {
            const response = await fetch('https://localhost:7023/api/UserList/all');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setUsers(data);
        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    // Para transferi işlemi
    const handleTransfer = async (event) => {
        event.preventDefault();

        const senderId = parseInt(document.getElementById('sender').value, 10);
        const receiverId = parseInt(document.getElementById('receiver').value, 10);
        const amount = parseFloat(document.getElementById('amount').value);

        const requestBody = {
            senderId: senderId,
            receiverId: receiverId,
            amount: amount,
        };

        try {
            const response = await fetch('https://localhost:7023/api/Transfer/transfer', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestBody),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            // Başarı durumunda kullanıcı listesini güncelle
            fetchUsers();

        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    // Kullanıcı ekleme işlemi
    const handleAddUser = async (event) => {
        event.preventDefault();

        const requestBody = {
            firstName: newUser.firstName,
            lastName: newUser.lastName,
            balance: parseFloat(newUser.balance),
        };

        try {
            const response = await fetch('https://localhost:7023/api/Users', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestBody),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            // Başarı durumunda kullanıcı listesini güncelle
            fetchUsers();
            setNewUser({
                firstName: '',
                lastName: '',
                balance: 0,
            });

        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
        }
    };

    // Form verilerini güncelle
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewUser({
            ...newUser,
            [name]: value,
        });
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    return (
        <div className="container">
            <div className="user-list">
                <h2>User List</h2>
                <ul>
                    {users.sort((a, b) => a.id - b.id).map(user => (
                        <li key={user.id}>
                            ID: {user.id} - {user.firstName} {user.lastName} - Balance: {user.balance}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="money-transfer">
                <h2>Money Transfer</h2>
                <form id="transferForm" onSubmit={handleTransfer}>
                    <div className="form-group">
                        <label htmlFor="sender">Sender:</label>
                        <input type="number" id="sender" placeholder="Enter sender's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="receiver">Receiver:</label>
                        <input type="number" id="receiver" placeholder="Enter receiver's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Amount:</label>
                        <input type="number" id="amount" placeholder="Enter amount" required />
                    </div>
                    <button type="submit">Transfer Money</button>
                </form>
            </div>
            <div className="add-user">
                <h2>Add User</h2>
                <form id="addUserForm" onSubmit={handleAddUser}>
                    <div className="form-group">
                        <label htmlFor="firstName">First Name:</label>
                        <input
                            type="text"
                            id="firstName"
                            name="firstName"
                            value={newUser.firstName}
                            onChange={handleInputChange}
                            placeholder="Enter first name"
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="lastName">Last Name:</label>
                        <input
                            type="text"
                            id="lastName"
                            name="lastName"
                            value={newUser.lastName}
                            onChange={handleInputChange}
                            placeholder="Enter last name"
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="balance">Balance:</label>
                        <input
                            type="number"
                            id="balance"
                            name="balance"
                            value={newUser.balance}
                            onChange={handleInputChange}
                            placeholder="Enter balance"
                            required
                        />
                    </div>
                    <button type="submit">Add User</button>
                </form>
            </div>
        </div>
    );
}

export default App;
