import React, { useState, useEffect } from 'react';
import { getUsers, transferMoney } from './services/api';

function App() {
  const [users, setUsers] = useState([]);
  const [senderId, setSenderId] = useState('');
  const [receiverId, setReceiverId] = useState('');
  const [amount, setAmount] = useState('');
  const [result, setResult] = useState('');


  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const usersData = await getUsers();
        console.log("userData : ",usersData)
        setUsers(usersData);
      } catch (error) {
        console.error('Kullanıcıları getirirken bir hata oluştu:', error);
      }
    };
    fetchUsers();
  }, []);

  const handleTransfer = async () => {
    try {
      const transferRequest = { senderId, receiverId, amount };
      console.log("TransferData : ",transferRequest)
      const resultData = await transferMoney(transferRequest);
      setResult('Transfer başarılı!');
      setResult(usersData);
    } catch (error) {
      setResult('Transfer başarısız.');
    }
  };

  return (
    <div style={{ display: 'flex', padding: '20px' }}>
      {}
      <div style={{ width: '50%', paddingRight: '20px' }}>
        <h2>Kullanıcı Listesi</h2>
        <ul>
          {users.map(user => (
            <li key={user.id}>{user.firstName},{user.lastName},{user.balance}</li>

          ))}
        </ul>
      </div>

      {}
      <div style={{ width: '50%' }}>
        <h2>Para Transferi</h2>
        <div>
          <label>Gönderen ID:</label>
          <input type="text" value={senderId} onChange={e => setSenderId(e.target.value)} />
        </div>
        <div>
          <label>Alıcı ID:</label>
          <input type="text" value={receiverId} onChange={e => setReceiverId(e.target.value)} />
        </div>
        <div>
          <label>Tutar:</label>
          <input type="text" value={amount} onChange={e => setAmount(e.target.value)} />
        </div>
        <button onClick={handleTransfer}>Transfer</button>
        <div>{result}</div>
      </div>
    </div>
  );
}

export default App;
