import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await fetch('http://localhost:7265/api/users'); // API endpoint'inizi buraya yazın
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                const userList = await response.json();
                setUsers(userList);
            } catch (error) {
                console.error('There was a problem with the fetch operation:', error);
            }
        };

        fetchUsers();
    }, []);

    const handleTransfer = async (event) => {
        event.preventDefault();

        const senderId = document.getElementById('sender').value;
        const receiverId = document.getElementById('receiver').value;
        const amount = document.getElementById('amount').value;

        const requestBody = {
            senderId: parseInt(senderId, 10),
            receiverId: parseInt(receiverId, 10),
            amount: parseFloat(amount),
        };

        try {
            const response = await fetch('http://localhost:7265/api/transfer/transfer', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestBody),
            });

            if (!response.ok) {
                const errorMessage = await response.text();
                throw new Error(`Network response was not ok: ${errorMessage}`);
            }

            const result = await response.json();
            console.log(result);

            // Başarı durumunda yapılacak işlemler
            alert('Transfer successful');

        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
            alert(`Transfer failed: ${error.message}`);
        }
    };

    return (
        <div className="container">
            <div className="user-list">
                <h2>User List</h2>
                <ul id="userList">
                    {users.map((user) => (
                        <li key={user.id}>
                            {user.name} - ${user.balance}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="money-transfer">
                <h2>Money Transfer</h2>
                <form id="transferForm" onSubmit={handleTransfer}>
                    <div className="form-group">
                        <label htmlFor="sender">Sender:</label>
                        <input type="number" id="sender" placeholder="Enter sender's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="receiver">Receiver:</label>
                        <input type="number" id="receiver" placeholder="Enter receiver's ID" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="amount">Amount:</label>
                        <input type="number" id="amount" placeholder="Enter amount" required />
                    </div>
                    <button type="submit">Transfer Money</button>
                </form>
            </div>
        </div>
    );
}

export default App;
